function loadWeather(t,o){$.simpleWeather({location:t,woeid:o,unit:"f",success:function(t){html='<h2><i class="icon-'+t.code+'"></i> '+t.temp+"&deg;"+t.units.temp+"</h2>",html+="<ul><li>"+t.city+", "+t.region+"</li>",html+='<li class="currently">'+t.currently+"</li>",html+="<li>"+t.alt.temp+"&deg;C</li></ul>",$("#weather").html(html)},error:function(t){$("#weather").html("<p>"+t+"</p>")}})}"geolocation"in navigator?$(".js-geolocation").show():$(".js-geolocation").hide(),$(".js-geolocation").on("click",function(){navigator.geolocation.getCurrentPosition(function(t){loadWeather(t.coords.latitude+","+t.coords.longitude)})}),$(document).ready(function(){loadWeather("Budapest","")});