function loadWeather(o,t){$.simpleWeather({location:o,woeid:t,unit:"c",success:function(o){html='<h2><i class="icon-'+o.code+'"></i> '+o.temp+"&deg;"+o.units.temp+"</h2>",html+='<li class="currently">'+o.currently+"</li>",$("#weather").html(html)},error:function(o){$("#weather").html("<p>"+o+"</p>")}})}"geolocation"in navigator?$(".js-geolocation").show():$(".js-geolocation").hide(),$(".js-geolocation").on("click",function(){navigator.geolocation.getCurrentPosition(function(o){loadWeather(o.coords.latitude+","+o.coords.longitude)})}),$(document).ready(function(){loadWeather("Badacsony","")});